
public class Pyramid2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int k = 1;

		for (int i = 0; i < 4; i++) {
			for (int j = i; j>=0; j--) {
				
				System.out.print(k); 
				System.out.print("\t");
				k++;
			}

			System.out.println("\n");

		}
		
		
		
	}

}
