

public class Pyramid3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
      	
		int k=1;
		for (int i=1; i <=4; i++) {
			for (int j=k; j<=i; j++) {
				
				System.out.print(j);
				System.out.print("\t");
			}
			
            	System.out.println("\n");

		}


	}

}
